<?phpnamespace Library\_Interface;use Library\Toolkit\Database;use Library\ValueObject\ValueObject;/**   Business core object interface class   This interface signs the business core class methods*/interface IBusinessCore {	/**        Constructor method signature        @param object $database        @return void	*/	public function __construct(Database $database = null);	/**        Create method signature        @param object $objVO        @return int	*/	public function create(ValueObject $objVO);	/**        Read method signature        @param object $objVO        @return object	*/	public function read(ValueObject $objVO);	/**        Save method signature        @param object $objVO        @return int	*/	public function save(ValueObject $objVO);	/**        Update method signature        @param object $objVO        @return void	*/	public function update(ValueObject $objVO);	/**        Delete method signature        @param object $objVO        @return void	*/	public function delete(ValueObject $objVO);	/**        list all method signature        @param object $objVO		@param array $options        @return object	*/	public function list_all(ValueObject $objVO = null, $options = []);	/**        Get database method signature        @param object $objVO        @return object	*/	public function get_database($db = null);}